package jogovelha.entidade;        public class Jogada{    private Jogador jog;    private int linha;    private int coluna;        public Jogada(int linha, int coluna, Jogador jog){         try{                  if(linha <= 2 && linha >= 0){                if(coluna <= 2 && coluna >= 0){                    if(!jog.getNome().equals("INDEFINIDO") && jog.getId() != 0){                        this.jog = jog;                        this.linha = linha;                        this.coluna = coluna;                    } else {                        System.err.println("Erro! Jogador informado é inválido!");                    }                } else {                    System.err.println("Erro! Linha coluna é inválida!");                }            } else {                System.err.println("Erro! Linha informada é inválida!");            }        } catch(Exception e){            System.err.println("Erro ao registrar jogada!");        }    }        public void mostrarNaConsole(){        System.out.println(            String.format(                """                ==JOGADA==                Jogador: %s                 Linha: %d                 Coluna: %d                ==========                """,                this.jog.getNome(),                this.linha,                this.coluna            )        );    }    public Jogador getJog() {        return jog;    }    public void setJog(Jogador jog) {        this.jog = jog;    }    public int getLinha() {        return linha;    }    public void setLinha(int linha) {        this.linha = linha;    }    public int getColuna() {        return coluna;    }    public void setColuna(int coluna) {        this.coluna = coluna;    }            }