package jogovelha.entidade;        public class Jogada{    private Jogador jog;    private int linha;    private int coluna;        public Jogada(int linha, int coluna, Jogador jog) throws Exception{               if(linha <= 2 && linha >= 0){            if(coluna <= 2 && coluna >= 0){                if(!jog.getNome().equals("INDEFINIDO") && jog.getId() != 0){                    this.linha = linha;                    this.coluna = coluna;                } else {                    throw new Exception("Erro! Jogador informado é inválido!");                }            } else {                throw new Exception("Erro! Linha coluna é inválida!");            }        } else {            throw new Exception("Erro! Linha informada é inválida!");        }                this.jog = jog;    }        public void mostrarNaConsole() throws Exception{        if(this.jog.getNome().equals("INDEFINIDO")){            throw new Exception("Jogada não definida!");                    } else {            System.out.printf(                """                ==JOGADA==                Jogador: %s                 Linha: %d                 Coluna: %d                ==========                """,                this.jog.getNome(),                this.linha,                this.coluna                                );        }    }    public Jogador getJog() {        return jog;    }    public void setJog(Jogador jog) {        this.jog = jog;    }    public int getLinha() {        return linha;    }    public void setLinha(int linha) {        this.linha = linha;    }    public int getColuna() {        return coluna;    }    public void setColuna(int coluna) {        this.coluna = coluna;    }            }